        "readUpdate _id": "string",
        "readOnly createdAtTimestamp": "Date",
        "readOnly lastModifiedTimestamp": "Date",
        "readWrite price": "number",
        "readOnly previousPrice": "number | null",
        "readWrite title": "string",
        "readWrite primaryImageDataUri": "string",
        "readOnly primaryImageThumbnailDataUri": "string",
        "readOnly state": "('forSale'|'reserved'|'sold')"
